export function getCategoryById(id: string): Category | undefined {
  return categories.find(c => c.id === id);
}

export function getScriptsByCategory(categoryId: string): TherapyScript[] {
  return scripts.filter(s => s.categoryId === categoryId).sort((a, b) => a.order - b.order);
}

export function getScriptById(id: string): TherapyScript | undefined {
  return scripts.find(s => s.id === id);
}

export function getAllScripts(): TherapyScript[] {
  return scripts.sort((a, b) => a.order - b.order);
}
